App.commentchannel = App.cable.subscriptions.create("CommentchannelChannel", {
  connected: function() {
    console.log("Connected to Comment Channel");
  },
  disconnected: function() {
    console.log("Disconnected from Comment Channel");
  },
  received: function(data) {
    var comments = $('#commentbox');
    comments.append(data['comment']);
    comments.scrollTop(comments[0].scrollHeight);
  }
});

